mapper.select:
select no, title, writer, content, regtime,comment_no
  from qna_board 
 where no = #{no}

mapper.insert:
insert into qna_board(
	title, writer, content
) values (
	#{title}, #{writer}, #{content}
)

mapper.update:
update qna_board 
   set title = #{title}, 
   	   writer = #{writer}, 
   	   content = #{content}
 where no = #{no}
